//Users service used for users REST endpoint
angular.module('mean').factory('Users', ['$resource',
    function($resource) {
        return $resource('/admin/users/:userId/:action', {
            userId: '@_id'
        }, {
            update: {
                method: 'PUT'
            },
            me: {
            	method: 'GET'
            },
            findById : {
                method: 'GET'
            },
            retard_moins_semaine : {
                method: 'GET',
                params: {action: 'retard_moins_semaine'},
                isArray: true
            }
        });
    }
]);
